mapper.select:
SELECT 

FROM biz_site_meterinfo a

WHERE a.id = #{id}

SELECT 

FROM biz_site_meterinfo a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.amsitenum = #{amsitenum}


		AND a.amnum = #{amnum}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM biz_site_meterinfo a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE biz_site_meterinfo SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
